  const [like, setLike] = useState(0);
  console.log("updated Like", like);
  const [dislike, setDislike] = useState(0);
  console.log("Updated Dislike", dislike);
  return (
    <div>
      <button
        onClick={() => {
          setLike(like + 1);
          console.log(like);
        }}
      >
        üëç {like}
      </button>
      <button
        onClick={() => {
          setDislike(dislike + 1);
        }}
      >
        üëé {dislike}
      </button>
      <Sample lk={like} dk={dislike} />
    </div>
  );
}
